import { useState } from "react"

export default function Cadastro() {
    const [prato, setPrato] = useState('')
    const [categoria, setCategoria] = useState('');
    const [descricao, setDescricao] = useState('')
    const [preco, setPreco] = useState('')

    const [imagem, setImagem] = useState(null)


    function salvar() {

        let obj = { prato, categoria, descricao, preco, imagem }

        fetch('http://localhost:8080/cardapio',
            {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(obj)
            })
            .then(x => alert('Contato criado com sucesso'))
    }

    function salvarImagem() {
        
    }

    function lerImagemChange(event) {
        const file = event.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {

                const Imagem64ToString = reader.result;
                setImagem(Imagem64ToString);

                // Aqui você pode enviar base64String para a sua API
                /* enviarParaAPI(base64String); */
            };
            reader.readAsDataURL(file);
        }
    }

    return (
        <div className="container">
            <h2 className="text-center">Novo prato para o cardápio</h2>

            <label className="form-label">Digite o nome do prato</label>
            <input className="form-control" type="text"
                placeholder="Nome do prato" value={prato} onChange={txt =>
                    setPrato(txt.target.value)} />


            <label className="form-label">Categoria</label>
            <select
                className="form-select"
                value={categoria}
                onChange={(txt) => setCategoria(txt.target.value)}
            >
                <option value="1">Entrada</option>
                <option value="2">Prato Principal</option>
                <option value="3">Acompanhamento</option>
                <option value="4">Sobremesa</option>
                <option value="5">Bebida</option>
            </select>

            <label className="form-label">Descrição do prato</label>
            <input className="form-control" type="text"
                placeholder="Informe a descrição" value={descricao}
                onChange={txt => setDescricao(txt.target.value)} />

            <label className="form-label">Preço do prato</label>
            <input className="form-control" type="number"
                placeholder="Informe o preço" value={preco}
                onChange={txt => setPreco(txt.target.value)} />

            {/*<label className="form-label">Insira a imagem do prato</label>
            <input className="form-control" type="file" placeholder="Insira uma imagem" 
                   id="imageUpload" 
                   name="imageUpload" onChange={lerImagem}/> */}

            <div>
                <label className="form-label">Insira a imagem do prato</label>
                <input className="form-control" type="file" onChange={lerImagemChange} />
                {imagem && <img src={imagem} alt="Converted" />}
            </div>

            <div className="mt-3 d-flex justify-content-between">
                <button onClick={salvar}
                    className="btn btn-primary">Salvar</button>
                <button className="btn btn-danger">Cancelar</button>
            </div>

        </div>
    )
}
