import { useState } from "react";
import { Link, useAsyncError } from "react-router-dom"

export default function Card(props) {
    const [prato] = useState('')
    const [categoria] = useState('')
    const [descricao] = useState('')
    const [preco] = useState('')
    const [imagem] = useState('')

    let obj = { prato, categoria, descricao, preco, imagem }

    function excluir() {
        fetch(`http://localhost:8080/cardapio/${props.cardapio.id}`,
            {
                method: 'DELETE',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(obj)
            })
            .then(x => alert('Contato excluído com sucesso'))

        console.log(props.cardapio.id)
    }

    return (
        <div className="card mt-3 col-sm-4 col-md-2">

            {/* <img src={props.cardapio.imagem} className="card-img-top" alt={props.cardapio.prato}></img> */}
            <div className="card-body">
                <div>
                    <span className="text-danger">{props.cardapio.id}</span>
                </div>
                <div>
                    <span className="fw-bold">Prato: </span>
                    <span className="text-danger">{props.cardapio.prato}</span>
                </div>

                <div>
                    <span className="fw-bold">Categoria: </span>
                    <span className="number-danger">{props.cardapio.categoria}</span>
                </div>

                <div>
                    <span className="fw-bold">Descrição: </span>
                    <span className="text-danger">{props.cardapio.descricao}</span>
                </div>

                <div>
                    <span className="fw-bold">Preco: </span>
                    <span className="number-danger">{props.cardapio.preco}</span>
                </div>

            </div>

            <div className="mt-3 d-flex justify-content-between">
                <Link to={`/cardapio/editar/${props.cardapio.id}`}><button className="btn btn-outline-primary">Editar</button></Link>

                <button onClick={excluir} className="btn btn-outline-danger">Excluir</button>
            </div>

        </div>


    )
}
